import React from 'react';
import ava from '../ava.png';
import './styles/profile.css';

function Profile(){

  const [content, setContent] = useState('message'); // Stores displayed content type ("message", "table", or "image")

  const handleClick = (type) => {
    setContent(type);
  };

  const renderContent = () => {
    switch (content) {
      case 'message':
        return <p>Hello world</p>;
      case 'table':
        return (
          <table>
            <tbody>
              <tr><td>Row 1, Col 1</td><td>Row 1, Col 2</td></tr>
              <tr><td>Row 2, Col 1</td><td>Row 2, Col 2</td></tr>
              <tr><td>Row 3, Col 1</td><td>Row 3, Col 2</td></tr>
              <tr><td>Row 4, Col 1</td><td>Row 4, Col 2</td></tr>
            </tbody>
          </table>
        );
      case 'image':
        return <img src="dog.jpeg" alt="Dog" />;
      default:
        return null;
    }
  }

    return (
    <div className='container'>
        <div className="bg-white rounded py-5 leftdiv">
        <div className='img'>
          <img src={ava} alt="" className='ava-img' />
          <div className='name'>McGonagal</div>
          <div className="edit-btn">Edit</div>
        </div>
      </div>
        <div className=' bg-white rounded py-5 rightdiv'>

      </div>
    </div>
    );
  }

export default Profile;
