import React, { useState } from 'react';
import Eth from '../eth.svg';
import Logo from '../Logo.png';
import { Link, useLocation } from 'react-router-dom';

function Header() {
  const location = useLocation();
  const isStorePage = location.pathname === '/store';

  // State to manage dropdown menu
  const [isDropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  return (
    <header>
      <div className="leftH">
        <img src={Logo} className="logo" alt="logo" />

        {/* Display dropdown button on smaller screens */}
        <div className="dropdown-btn" onClick={toggleDropdown}>
          <div className="headerItem">Menu</div>
          {isDropdownOpen && (
            <div className="dropdown-content">
              <Link to="/" className="link">
                <div className="headerItem">Swap</div>
              </Link>
              <Link to="/tokens" className="link">
                <div className="headerItem">Token</div>
              </Link>
              <Link to="/store" className="link">
                <div className="headerItem">Store</div>
              </Link>
            </div>
          )}
        </div>

        {/* Display individual buttons on larger screens */}
        {!isDropdownOpen && (
          <>
            <Link to="/" className="link">
              <div className="headerItem">Swap</div>
            </Link>
            <Link to="/tokens" className="link">
              <div className="headerItem">Token</div>
            </Link>
            <Link to="/store" className="link">
              <div className="headerItem">Store</div>
            </Link>
          </>
        )}
      </div>

      <div className="rightH">
        {/* rest of your code */}
      </div>
    </header>
  );
}

export default Header;
