import "./styles/ProductDetail.css";
import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import ball1 from '../ball1.png';
import shoeImage from '../shoe.png';
import shirtImage from '../shirt.png';
import { Link, useParams } from 'react-router-dom'; // Import Link from react-router-dom


function ProductDetail() {
    const { id } = useParams();

    // Ideally, fetch product data from a data source or API
    const products = [{
            id: '1',
            image: ball1,
            description: 'World 2023',
            title: "Messi's ball",
            price: 5,
        },
        {
            id: '2',
            image: shoeImage,
            description: 'Running Shoes',
            title: 'Nike Air Zoom',
            price: 8,
        },
        {
            id: '3',
            image: shirtImage,
            description: 'Casual Shirt',
            title: 'Cotton Comfort',
            price: 12,
        },];
    const selectedProduct = products.find((product) => product.id === id);

    if (!selectedProduct) {
        return <div>Product not found</div>;
    }

    return (
        <div className="product-detail">
            <div>
            <img className="product-image" src={selectedProduct.image} alt={selectedProduct.title} />
            <h2 className="product-title">{selectedProduct.title}</h2>
            </div>
            <div>
            <p className="product-description">{selectedProduct.description}</p>
            <p className="product-price">{`${selectedProduct.price} ETH`}</p>
            </div>
            {/* Add additional details and buttons as shown in the image */}
            <div className="buttons">
                <button className="btn btn-primary add-to-cart">Add to Cart</button>
                <button className="btn buy-now">Buy Now</button>
            </div>
        </div>
    );
}

export default ProductDetail;
