import React, {useState, useEffect} from 'react'
import {
  Input,
  Radio,
  message,
  Modal,
  Popover,
} from 'antd';
import { ArrowDownIcon, SettingsIcon } from '@chakra-ui/icons';
import tokenList from '../tokenList.json'

function Swap() {

  const[slippage, setSlippage] = useState(0.5);
  const[tokenOneAmount, setTokenOneAmount] = useState(null);
  const[tokenTwoAmount, setTokenTwoAmount] = useState(null);

  function slippageChangeValue(x){
    setSlippage(x.target.value)
  }

  function changeTokenAmount(x){
    setTokenOneAmount(x.target.value)
  }

  const settingContent = (
    <>
      <div> Slippage tolerrence </div>
      <div>
        <Radio.Group value={slippage} onChange={slippageChangeValue}>
          <Radio.Button value = {0.5}>0.5%</Radio.Button>
          <Radio.Button value = {2.5}>2.5%</Radio.Button>
          <Radio.Button value = {5}>5.0%</Radio.Button>
        </Radio.Group>
      </div>
    </>

  );

  return (
    <div className="content">
      <div className='tradeBox'>
        <div className="tradeBoxHeader">
          <h3>Swap</h3>
          <Popover title="Setting" content={settingContent} trigger="click" placement='bottomRight'>
            <SettingsIcon className='cog' />
          </Popover>
        </div>
        <div className="inputs">
          <Input placeholder='0.00' value={tokenOneAmount} onChange={changeTokenAmount}/>
          <div className="asset assetOne">
            <img src={tokenOne.img} alt="assetOneLogo" className='assetLogo' />
          </div>
          <Input placeholder='0.00' value={tokenTwoAmount} disabled = {true}/>
          <div className="asset assetTwo">
            <img src={tokenTwo.img} alt="assetTwoLogo" className='assetLogo' />
            {t}
          </div>
        </div>
      </div>
    </div>

  )
}

export default Swap
