import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { useParams, useState, useEffect } from 'react';

function ProductDetail({ match }) {
    const [selectedProduct, setSelectedProduct] = useState(null);

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const productId = parseInt(match.params.id, 10);
                // Replace with your actual product fetching logic
                const product = await fetchProductData(productId);
                setSelectedProduct(product);
            } catch (error) {
                console.error('Error fetching product:', error);
            }
        };

        fetchProduct();
    }, [match.params.id]);
    return (
        <div className="product-item">
            <img src={product.image} alt={product.title} className="product-image" />
            <h3>{product.title}</h3>
            <p>{product.description}</p>
            <div className="price-buttons">
                <p className="price">{`${product.price} ETH`}</p>
                <Link to={`/product/${product.id}`} className="buy-now-button">
                    Buy Now
                </Link>
                <button className="add-to-cart-button">Add to Cart</button>
            </div>
            <div className="reviews">
                {/* Add logic to display reviews here */}
            </div>
        </div>
    );
}

ProductItem.propTypes = {
    product: PropTypes.shape({
        id: PropTypes.number.isRequired,
        image: PropTypes.string.isRequired,
        description: PropTypes.string.isRequired,


        title: PropTypes.string.isRequired,
        price: PropTypes.number.isRequired,
    }).isRequired,
};

export

    default ProductItem;
